import './index.css';
import App from './App';
import React, { Fragment } from "react";
import ReactDOM from "react-dom";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import { Switch, Route, Link, BrowserRouter } from "react-router-dom";

import "./styles.css";

function AppTabs() {
    return (
      <BrowserRouter>
        <div className="Tabs">
          <Route
            path="/"
            render={({ location }) => (
              <Fragment>
                <Tabs value={location.pathname}>
                  <Tab label="Item One" component={Link} to="/" />
                  <Tab label="Item Two" component={Link} to="/tab2" />
                  <Tab
                    label="Item Three"
                    href="#basic-tabs"
                    component={Link}
                    to="/tab3"
                  />
                </Tabs>
                <Switch>
                  <Route path="/tab2" render={() => <div>Tab 2</div>} />
                  <Route path="/tab3" render={() => <div>Tab 3</div>} />
                  <Route path="/" render={() => <div><App/></div>} />
                </Switch>
              </Fragment>
            )}
          />
        </div>
      </BrowserRouter>
    );
  }

ReactDOM.render(<AppTabs />, document.getElementById('root'));
