import React, { useContext, useState, useEffect } from 'react';
import Item from './Item';
import { ThemeContext, PodcastContext } from './app-context';
import ReactTable from 'react-table';
import "react-table/react-table.css";

async function fetchCatBreedsAPI() {
  try{
    const res = await fetch('https://api.thecatapi.com/v1/breeds');
    const data = await res.json();
    const result = data.map(a => {return {'description' : a.description, 'name' : a.name }});    

    const dataImages = await data.map(async a => {    
        console.log('asd')

      const res = await fetch(`https://api.thecatapi.com/v1/images/search?breed_ids=${a.id}`)
      const data = await res.json();
      const [url] = data;    

      return {'url' : url}
    });

    
    console.log(dataImages)
    // console.log(data)

    const resultImages = dataImages.map(a => {return {'description' : a.description }});    

console.log(result)
    return result;
  }catch(err){
    console.log(err)
 }
}

async function fetchCatImagesAPI(limit) {
  try{
    const res = await fetch(`https://api.thecatapi.com/v1/images/search?limit=${limit}`);
    const data = await res.json();
    const result = data.map(a => [a.name, a.description]);    
    // console.log(data)
  }catch(err){
    console.log(err)
 }
}

export const TableCats = (data) => {
  // const data = [{
  //   name: 'Tanner Linsley',
  //   age: 26,
  //   friend: {
  //     name: 'Jason Maurer',
  //     age: 23,
  //   }
  // }];
 
  const columns = [{
    Header: 'Photo',
    accessor: 'name' // String-based value accessors!
  }, {
    Header: 'Name',
    accessor: 'description',
    Cell: props => <span className='number'>{props.value}AAA</span> // Custom cell components!
  }, {
    id: 'friendName', // Required because our accessor is not a string
    Header: 'Description',
    accessor: d => d.friend.name // Custom value accessors!
  }, {
    Header: props => <span>Friend Age</span>, // Custom header components!
    accessor: 'friend.age'
  }]
 
  return (
      <ReactTable
        data={data}
        columns={columns}
        defaultPageSize={20}
        className="-striped -highlight"   
      />
  );
}

const App = () => {
  const podcast = useContext(PodcastContext);
  const theme = useContext(ThemeContext);
  const [count, setCount] = useState(0);
  const [value, changeValue] = useState('Test value');
 
  useEffect(() => {
    fetchCatBreedsAPI();
    fetchCatImagesAPI();
  }, []);

  const handleValueChange = (e) => {
    changeValue(e.target.value);
  }  

  return (
    <div className={`card ${theme}`}>
        {TableCats()}
      <Item label="Podcast">
        <h4>{podcast}</h4>
      </Item>
      <Item label="TableBreeds">
      
      </Item>

      <Item label={`Counter value is ${count}`}>
        <button onClick={() => setCount(count + 1)}>Click me</button>
      </Item>

      <Item label={`${value}`}>
        <input
          value={value}
          onChange={handleValueChange}
        />
      </Item>
{/* 
      <Item label="Fetch User">
        <img src={cat} alt="Logo" />;
      </Item> */}

    </div>
  );
};

export default App;
