import React, {Fragment} from 'react';
import ReactDOM from 'react-dom';

import './index.css';
import App from './App';

import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";

import { Switch, Route, Link, BrowserRouter } from "react-router-dom";

function TabsApp() {
    return (
      <BrowserRouter>
        <div className="App">
          <Route
            path="/"
            render={({ location }) => (
              <Fragment>
                <Tabs value={location.pathname}>
                  <Tab label="Item One" component={Link} to="/" />
                  <Tab label="Item Two" component={Link} to="/tab2" />
                  <Tab
                    label="Item Three"
                    href="#basic-tabs"
                    component={Link}
                    to="/tab3"
                  />
                </Tabs>
                <Switch>
                  <Route path="/tab2" render={() => <div>Tab 2</div>} />
                  <Route path="/tab3" render={() => <div>Tab 3</div>} />
                  <Route path="/" render={() => <div><App/></div>} />
                </Switch>
              </Fragment>
            )}
          />
        </div>
      </BrowserRouter>
    );
  }

  const UsersPage = ({ match }) => {
    return (
      <div>
        <NavTab to="/admins">Admins</NavTab>
        <NavTab to="/moderators">Moderators</NavTab>
        <NavTab to="/users">Users</NavTab>
  
        <Switch>
          <Route
            exact
            path={`${match.path}`}
            render={() => <Redirect replace to={`${match.path}/admins`} />}
          />
          <Route path={`${match.path}/admins`} component={Admins} />
          <Route path={`${match.path}/moderators`} component={Moderators} />
          <Route path={`${match.path}/users`} component={Users} />
        </Switch>
      </div>
    );
  };

ReactDOM.render(<TabsApp />, document.getElementById('root'));
