export async function fetchCatImage(a) {
    try{
        const res = await fetch(`https://api.thecatapi.com/v1/images/search?breed_ids=${a.id}`)
        const data = await res.json();
        const [url] = data; 
        return {'url' : url};
    }catch(err){
      console.log(err)
   }
  }
  
  export async function fetchCatBreeds() {
    try{
      const res = await fetch(`https://api.thecatapi.com/v1/breeds`);
      const data = await res.json();
      const pArray = data.map(a => fetchCatImage(a)); 
      const users = await Promise.all(pArray);

      const result = data.map(function(item, i, arr) {
        alert( item['name']  );
      });

      console.log(data)

      return users;
    }catch(err){
      console.log(err)
   }
  }
