import React, {Fragment} from 'react';
import ReactDOM from 'react-dom';

import './index.css';
import App from './App';

import { BrowserRouter, Route, Switch, NavLink, Link, } from 'react-router-dom';
// import Home from './home';
// import Users from './Users';
// import Posts from './Posts';

import './styles.css';

const TabsApp = () => (
  <BrowserRouter>
    <div>
      <header>
        <ul className="nav-list">
          <li className="nav-list__item">
            <NavLink exact className="nav-link" to="/tab1">Home</NavLink>
          </li>
          <li className="nav-list__item">
            <NavLink className="nav-link" to="/tab2">Users</NavLink>
          </li>
          <li className="nav-list__item">
            <NavLink className="nav-link" to="/tab3">Blog posts</NavLink>
          </li>
        </ul>
      </header>
      <main>
        <Switch>
          {/* <Route exact path="/" component={Home} />

          <Route path="/users" component={Users} /> */}
          <Route path="/tab1" render={() => <div>Tab 1</div>} />
          <Route path="/tab2" render={() => <div>Tab 2</div>} />
          <Route path="/tab3" render={() => <div>Tab 3</div>} />
         
        </Switch>
      </main>
    </div>
  </BrowserRouter>
);


