import React, { useContext, useState, useEffect } from 'react';
import Item from './Item';
import { ThemeContext, PodcastContext } from './app-context';
import ReactTable from 'react-table';
import "react-table/react-table.css";
import { fetchCatBreeds} from './API'

const columns = [{
  Header: 'Photo',
  accessor: 'url' // String-based value accessors!
}, {
  Header: 'Name',
  accessor: 'name',
  Cell: props => <span className='number'>{props.value}AAA</span> // Custom cell components!
}, {
  id: 'friendName', // Required because our accessor is not a string
  Header: 'Description',
  accessor: 'description'
}]

  const dataS = [{
    name: 'Tanner Linsley',
    age: 26,
    name: 'Jason Maurer'
  }];

export const TableCats = ({data}) => 
  (
      <ReactTable
        data={data === null && dataS}
        columns={columns} 
        defaultPageSize={20}
        className="-striped -highlight"   
      />
  );

const getDataCats = () =>{
    const [dataTable, setData] = useState(null);
 
    useEffect(() => {
        fetchCatBreeds().then((result) => {
        // you can access the result from the promise here
        console.log(  result )
        setData(result);
    });
    });
    return dataTable;
}

const App = () => {
  const podcast = useContext(PodcastContext);
  const theme = useContext(ThemeContext);
  const [count, setCount] = useState(0);
  const [value, changeValue] = useState('Test value');
  const data = getDataCats(); 

  const handleValueChange = (e) => {
    changeValue(e.target.value);
  }  

  return (
    <div className={`card ${theme}`}>
       <Item>
       <TableCats data = {data}/>
       </Item> 
      <Item label="Podcast">
        <h4>{podcast}</h4>
      </Item>
      <Item label="TableBreeds">
      
      </Item>

      <Item label={`Counter value is ${count}`}>
        <button onClick={() => setCount(count + 1)}>Click me</button>
      </Item>

      <Item label={`${value}`}>
        <input
          value={value}
          onChange={handleValueChange}
        />
      </Item>
    </div>
  );
};

export default App;
