import React, {Fragment} from 'react';
import ReactDOM from 'react-dom';

import './index.css';
import App from './App';

import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";

import { Switch, Route, Link, BrowserRouter } from "react-router-dom";

function TabsApp() {
    return (
      <BrowserRouter>
        <div className="App">
          <Route
            path="/"
            render={({ location }) => (
              <Fragment>
                <Tabs value={location.pathname}>
                  <Tab label="Item One" component={Link} to="/tab1" />
                  <Tab label="Item Two" component={Link} to="/tab2" />
                  <Tab
                    label="Item Three"
                    href="#basic-tabs"
                    component={Link}
                    to="/tab3"
                  />
                </Tabs>
                <Switch>
                  <Route path="/tab2"  component={App}/>
                  <Route path="/tab3"  component={App} />
                  <Route path="/tab1" component={App} />
                </Switch>
              </Fragment>
            )}
          />
        </div>
      </BrowserRouter>
    );
  }

