import React, { useContext, useState, useEffect } from 'react';
import Item from './Item';
import { ThemeContext, PodcastContext } from './app-context';
import ReactTable from 'react-table';
import "react-table/react-table.css";

export const TableCats = () => {
  const data = [{
    name: 'Tanner Linsley',
    age: 26,
    friend: {
      name: 'Jason Maurer',
      age: 23,
    }
  }];
 
  const columns = [{
    Header: 'Photo',
    accessor: 'name' // String-based value accessors!
  }, {
    Header: 'Name',
    accessor: 'age',
    Cell: props => <span className='number'>{props.value}</span> // Custom cell components!
  }, {
    id: 'friendName', // Required because our accessor is not a string
    Header: 'Friend Name',
    accessor: d => d.friend.name // Custom value accessors!
  }, {
    Header: props => <span>Friend Age</span>, // Custom header components!
    accessor: 'friend.age'
  }]
 
  return (
    <div>
      <br />
      <strong>Note: Having the console open will slow performance</strong>
      <br />
      <br />

      <ReactTable
        data={data}
        columns={columns}
        defaultPageSize={10}
        className="-striped -highlight"
   
      />
      <br />
    </div>
  );
}

const App = () => {
  const podcast = useContext(PodcastContext);
  const theme = useContext(ThemeContext);
  const [count, setCount] = useState(0);
  const [value, changeValue] = useState('Test value');
  const [width, setWidth] = useState(window.innerWidth);
  const [cat, setImage] = useState(false);

  const handleValueChange = (e) => {
    changeValue(e.target.value);
  }

  useEffect(() => {
    document.title = value;
  });

  useEffect(() => {
    const handleWidth = () => setWidth(window.innerWidth)
    window.addEventListener('resize', handleWidth);
    return () => {
      window.removeEventListener('resize', handleWidth);
    };
  });

  async function fetchCatBreedsAPI() {
    try{
      const res = await fetch('https://api.thecatapi.com/v1/breeds');
      const data = await res.json();
      const [udata] = data;
      const url = udata.url;
      console.log(data)

      setImage(url);  
    }catch(err){
      console.log(err)
   }
  }
  
  useEffect(() => {
    fetchCatBreedsAPI();
  }, []);

  return (
    <div className={`card ${theme}`}>
        {TableCats()}
      <Item label="Podcast">
        <h4>{podcast}</h4>
      </Item>
      <Item label="TableBreeds">
      
      </Item>

      <Item label={`Counter value is ${count}`}>
        <button onClick={() => setCount(count + 1)}>Click me</button>
      </Item>

      <Item label={`${value}`}>
        <input
          value={value}
          onChange={handleValueChange}
        />
      </Item>

      <Item label={`Browser window is`}>
        <h4>{width}</h4>
      </Item>

      <Item label="Fetch User">
        <img src={cat} alt="Logo" />;
        {/* <div>{user ? `${user.name.first} ${user.name.last}` : ''}</div> */}
      </Item>

    </div>
  );
};

export default App;
