import React, { useContext, useState, useEffect } from 'react';
import Item from './Item';
import { ThemeContext, PodcastContext } from './app-context';
import ReactTable from 'react-table';
import "react-table/react-table.css";
import { fetchCatBreeds} from './API';
import matchSorter from 'match-sorter'

  const dataS = [{
    description: 'Jason Maurer',
    name: 'Tanner Linsley',
    url: '26'
  }];

export const TableCats = ({data}) => 
  { 
    const dataPreview = data === null ? dataS : data;
    const pNames = dataPreview.map(a => a.name);
    
    return (
      <ReactTable
        data={dataPreview} 
        defaultPageSize={20}
        className="-striped -highlight"   
        filterable
        columns = {[{
            Header: 'Name',
            accessor: 'url' ,
            filterMethod: (filter, rows) =>
              matchSorter(rows, filter.value, { keys: ["name"] }),
            filterAll: true ,
            Cell: props => <img src={props.value} alt={'Image...'} width='200px' height='200px'/> // Custom cell components!
            }, {
            Header: 'Breed',
            accessor: 'name',
            filterMethod: (filter, row) => {
              if (filter.value === "all") {
                return true;
              }
              return row[filter.id] === filter.value;
            },
            Filter: ({ filter, onChange }) =>{
              return (
              <select
                onChange={event => onChange(event.target.value)}
                style={{ width: "100%" }}
                value={filter ? filter.value : "all"}
              > 
                <option value="all">Show All</option>
                {
                  pNames.map( k => {
                  return <option key={k.toString()} value={k}>{k}</option>
                  })
                }
              </select>)
            } 
            }, {
            Header: 'Description',
            accessor: 'description'
            }]}
      />
  );}

const getDataCats = () =>{
    const [dataTable, setData] = useState(null);
 
    useEffect(() => {
        fetchCatBreeds().then((result) => {
        setData(result);
    });
    }, []);
    return dataTable;
}

const App = () => {
  const podcast = useContext(PodcastContext);
  const theme = useContext(ThemeContext);
  const [count, setCount] = useState(0);
  const [value, changeValue] = useState('');
  const data = getDataCats(); 

  const handleValueChange = (e) => {
    changeValue(e.target.value);
  }  

  return (
    <div className={`card ${theme}`}>    
      <Item label={`Search by Name`}>
        <input
          value={value}
          onChange={handleValueChange}
        />
      </Item>

       <Item>
       <TableCats data = {data}/>
       </Item> 

      <Item label="Podcast">
        <h4>{podcast}</h4>
      </Item>

      <Item label={`Counter value is ${count}`}>
        <button onClick={() => setCount(count + 1)}>Click me</button>
      </Item>

    </div>
  );
};

export default App;
